import React, { Component, Fragment } from 'react'
import { Container, Row, Col, Card } from 'react-bootstrap'
import Slider from 'react-slick'
import 'slick-carousel/slick/slick.css'
import 'slick-carousel/slick/slick-theme.css'
import AppURL from '../../api/AppURL'
import axios from 'axios'
import NewArrivalLoading from '../PlaceHolder/NewArrivalLoading'
import { Link } from 'react-router-dom'

class NewArrival extends Component {
  constructor(props) {
    super(props)
    this.state = {
      ProductData: [],
      isLoading: '',
      mainDiv: 'd-none',
    }
    this.next = this.next.bind(this)
    this.previous = this.previous.bind(this)
  }
  next() {
    this.slider.slickNext()
  }
  previous() {
    this.slider.slickPrev()
  }

  componentDidMount() {
    axios
      .get(AppURL.ProductListByRemark('NEW'))
      .then((response) => {
        this.setState({
          ProductData: response.data,
          isLoading: 'd-none',
          mainDiv: '',
        })
      })
      .catch((error) => {})
  }

  render() {
    const NewList = this.state.ProductData
    const MyView = NewList.map((NewList) => {
      ;<div>
        <Link className="text-link" to={'/productdetails/' + NewList.id}>
          <Card className="image-box card">
            <img className="center" src={NewList.image} />
            <Card.Body>
              <p className="product-name-on-card">{NewList.title}</p>
              <p className="product-price-on-card">Price : ${NewList.price}</p>
            </Card.Body>
          </Card>
        </Link>
      </div>
    })

    var settings = {
      dots: false,
      infinite: true,
      speed: 500,
      autoplay: true,
      autoplaySpeed: 3000,
      slidesToShow: 4,
      slidesToScroll: 1,
      initialSlide: 0,
      arrows: false,
      responsive: [
        {
          breakpoint: 1024,
          settings: {
            slidesToShow: 4,
            slidesToScroll: 1,
            infinite: true,
            dots: true,
          },
        },
        {
          breakpoint: 600,
          settings: {
            slidesToShow: 2,
            slidesToScroll: 1,
            initialSlide: 2,
          },
        },
        {
          breakpoint: 480,
          settings: {
            slidesToShow: 1,
            slidesToScroll: 1,
          },
        },
      ],
    }

    return (
      <Fragment>
        <NewArrivalLoading isLoading={this.state.isLoading} />

        <div className={this.state.mainDiv}>
          <Container className="text-center" fluid={true}>
            <div className="section-title text-center mb-55">
              <h2>
                NEW ARRIVAL &nbsp;
                <a className="btn btn-sm ml-2 site-btn" onClick={this.previous}>
                  <i className="fa fa-angle-left"></i>
                </a>
                &nbsp;
                <a className="btn btn-sm ml-2 site-btn" onClick={this.next}>
                  <i className="fa fa-angle-right"></i>
                </a>
              </h2>
              <p>Some Of Our Exclusive Collection, You May Like</p>
            </div>

            <Row>
              <Slider ref={(c) => (this.slider = c)} {...settings}>
                {MyView}
              </Slider>
            </Row>
          </Container>
        </div>
      </Fragment>
    )
  }
}

export default NewArrival
